import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [images, setImages] = useState([
    {
      id: 1,
      src: "image1.jpg",
      isFeature: true,
    },
    {
      id: 2,
      src: "image2.jpg",
      isFeature: false,
    },
    // Add more images here
  ]);

  const [selectedImages, setSelectedImages] = useState([]);
  const [draggedImage, setDraggedImage] = useState(null);

  const handleImageClick = (image) => {
    if (selectedImages.includes(image)) {
      setSelectedImages((prev) => prev.filter((i) => i !== image));
    } else {
      setSelectedImages([...selectedImages, image]);
    }
  };

  const handleImageDragStart = (image) => {
    setDraggedImage(image);
  };

  const handleImageDragOver = (image) => {
    if (draggedImage !== image) {
      const newImages = [...images];
      const draggedIndex = newImages.findIndex((i) => i === draggedImage);
      const newIndex = newImages.findIndex((i) => i === image);

      // Rearrange the images
      newImages.splice(draggedIndex, 1);
      newImages.splice(newIndex, 0, draggedImage);

      setImages(newImages);
      setDraggedImage(null);
    }
  };

  const setFeatureImage = (image) => {
    const newImages = images.map((i) => ({
      ...i,
      isFeature: i === image,
    }));
    setImages(newImages);
  };

  const handleDeleteImages = () => {
    const remainingImages = images.filter((image) => !selectedImages.includes(image));
    setImages(remainingImages);
    setSelectedImages([]);
  };

  return (
    <div className="App">
      <div className="gallery">
        {images.map((image) => (
          <div
            key={image.id}
            className={`image ${image.isFeature ? "feature" : ""} ${selectedImages.includes(image) ? "selected" : ""}`}
            onClick={() => handleImageClick(image)}
            draggable
            onDragStart={() => handleImageDragStart(image)}
            onDragOver={() => handleImageDragOver(image)}
          >
            <img src={image.src} alt={`Image ${image.id}`} />
            <button className="feature-button" onClick={() => setFeatureImage(image)}>
              Set as Feature
            </button>
          </div>
        ))}
      </div>
      {selectedImages.length > 0 && (
        <button className="delete-button" onClick={handleDeleteImages}>
          Delete Selected
        </button>
      )}
    </div>
  );
};

export default App;
