import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [selectedImages, setSelectedImages] = useState([]);

  const onSelectFile = (event) => {
    const selectedFiles = event.target.files;

    // Check if selectedFiles is an array and not empty
    if (selectedFiles && selectedFiles.length > 0) {
      const selectedFilesArray = Array.from(selectedFiles);

      const imagesArray = selectedFilesArray.map((file) => {
        return URL.createObjectURL(file);
      });

      setSelectedImages((previousImages) => previousImages.concat(imagesArray));

      // Reset the input field to allow selecting the same files again
      event.target.value = "";
    }
  };

  function deleteHandler(image) {
    setSelectedImages((previousImages) => previousImages.filter((e) => e !== image));
    URL.revokeObjectURL(image);
  }

  return (
    <section>
      <label>
        + Add Images
        <br />
        <span>up to 10 images</span>
        <input
          type="file"
          name="images"
          onChange={onSelectFile}
          multiple
          accept="image/png, image/jpeg, image/webp"
        />
      </label>
      <br />

      {selectedImages.length > 0 &&
        (selectedImages.length > 100 ? (
          <p className="error">
            You can't upload more than 100 images! <br />
            <span>
              please delete <b> {selectedImages.length - 100} </b> of them{" "}
            </span>
          </p>
        ) : (
          <button
            className="upload-btn"
            onClick={() => {
              console.log(selectedImages);
            }}
          >
            UPLOAD {selectedImages.length} IMAGE
            {selectedImages.length === 1 ? "" : "S"}
          </button>
        ))}

      <div className="images">
        {selectedImages &&
          selectedImages.map((image, index) => (
            <div key={image} className="image">
              <img src={image} height="200" alt="upload" />
              <button onClick={() => deleteHandler(image)}>
                DELETE
              </button>
              <p>{index + 1}</p>
            </div>
          ))}
      </div>
    </section>
  );
};

export default App;
